mapper.select:
SELECT

FROM logging_operate ali

    ali.del_flag = 0

        AND ali.operate_type = #{query.operateType, jdbcType=INTEGER}


        AND LOCATE(#{query.userName, jdbcType=VARCHAR}, ali.user_name) > 0


        AND LOCATE(#{query.logCode, jdbcType=VARCHAR}, ali.log_code) > 0


        AND LOCATE(#{query.requestIp, jdbcType=VARCHAR}, ali.request_ip) > 0


        AND LOCATE(#{query.requestUrl, jdbcType=VARCHAR}, ali.request_url) > 0


        AND ali.request_client_code = #{query.requestClientCode, jdbcType=VARCHAR}


        AND ali.operate_status = #{query.operateStatus, jdbcType=INTEGER}


        AND LOCATE(#{query.dataSources, jdbcType=VARCHAR}, ali.data_sources) > 0


        AND ali.request_start_time >= #{query.startTime, jdbcType=TIMESTAMP}


        AND ali.request_start_time <= #{query.endTime, jdbcType=TIMESTAMP}

mapper.delete:
DELETE
FROM logging_operate
WHERE operate_id = #{id, jdbcType=VARCHAR}

mapper.update:
UPDATE logging_operate

    del_flag = 1,

WHERE operate_id = #{operateId, jdbcType=VARCHAR}
