ali.del_flag = 0 AND ali.model_id = #{query.modelId, jdbcType=VARCHAR}
AND ( LOCATE(#{query.keyword, jdbcType=VARCHAR}, ali.diagram_names) > 0 OR LOCATE(#{query.keyword, jdbcType=VARCHAR}, acpc.category_name) > 0 OR LOCATE(#{query.keyword, jdbcType=VARCHAR},ali.deployment_name) > 0 )

