mapper.select:
SELECT , acpc.category_name FROM act_custom_design_model ali LEFT JOIN act_custom_process_category acpc ON ali.category = acpc.category_code
ali.del_flag = 0
AND LOCATE(#{query.diagramNames, jdbcType=VARCHAR}, ali.diagram_names) > 0
AND LOCATE(#{query.category, jdbcType=VARCHAR}, ali.category) > 0
AND ali.model_state = #{query.modelState, jdbcType=INTEGER}
AND ali.create_time >= #{query.startTime, jdbcType=TIMESTAMP}
AND ali.create_time <= #{query.endTime, jdbcType=TIMESTAMP}

SELECT count( model_id ) FROM act_custom_design_model WHERE del_flag =0
AND model_id != #{id, jdbcType=VARCHAR}
AND LOCATE(#{key, jdbcType=VARCHAR}, process_definition_keys) > 0

mapper.delete:
DELETE
FROM act_custom_design_model
WHERE model_id = #{id, jdbcType=VARCHAR}

mapper.update:
UPDATE act_custom_design_model
del_flag = 1, unique_help = model_id,
WHERE model_id = #{modelId, jdbcType=VARCHAR}
