mapper.select:
SELECT , scd.dict_name FROM sys_common_dict_item ali LEFT JOIN sys_common_dict scd ON ali.dict_id = scd.dict_id
ali.del_flag = 0
AND ( LOCATE(#{query.keyword, jdbcType=VARCHAR}, ali.item_text) > 0 OR LOCATE(#{query.keyword, jdbcType=VARCHAR}, scd.dict_name) > 0 OR LOCATE(#{query.keyword, jdbcType=VARCHAR}, scd.dict_code) > 0 )
AND ali.dict_id = #{query.dictId, jdbcType=VARCHAR}
AND ali.item_status = #{query.itemStatus, jdbcType=INTEGER}
AND ali.create_time >= #{query.startTime, jdbcType=TIMESTAMP}
AND ali.create_time <= #{query.endTime, jdbcType=TIMESTAMP}

mapper.delete:
DELETE
FROM sys_common_dict_item
WHERE item_id = #{id, jdbcType=VARCHAR}

mapper.update:
UPDATE sys_common_dict_item
del_flag = 1, unique_help = item_id,
WHERE item_id = #{itemId, jdbcType=VARCHAR}
