mapper.select:
SELECT

FROM sys_rbac_org ali

     ali.del_flag = 0

     AND LOCATE(#{query.orgName, jdbcType=VARCHAR}, ali.org_name) > 0


     AND LOCATE(#{query.orgCode, jdbcType=VARCHAR}, ali.org_code) > 0


     AND ali.org_status = #{query.orgStatus, jdbcType=INTEGER}


     AND LOCATE(#{query.socialCode, jdbcType=VARCHAR}, ali.social_code) > 0


     AND ali.create_time >= #{query.startTime, jdbcType=TIMESTAMP}


     AND ali.create_time <= #{query.endTime, jdbcType=TIMESTAMP}

mapper.delete:
DELETE
FROM sys_rbac_org
WHERE org_id = #{id, jdbcType=VARCHAR}

mapper.update:
UPDATE sys_rbac_org
del_flag = 1, unique_help = org_id,
WHERE org_id = #{orgId, jdbcType=VARCHAR}
