mapper.select:
SELECT

FROM act_custom_design_form_detail ali

    ali.del_flag = 0

        AND ali.form_detail_id = #{query.formDetailId, jdbcType=VARCHAR}


        AND ali.from_filed_name = #{query.fromFiledName, jdbcType=VARCHAR}


        AND ali.form_filed_type = #{query.formFiledType, jdbcType=INTEGER}


        AND ali.form_filed_label = #{query.formFiledLabel, jdbcType=VARCHAR}


        AND ali.form_filed_must = #{query.formFiledMust, jdbcType=INTEGER}


        AND ali.form_filed_error_msg = #{query.formFiledErrorMsg, jdbcType=VARCHAR}


        AND ali.form_filed_sort = #{query.formFiledSort, jdbcType=INTEGER}


        AND ali.form_filed_state = #{query.formFiledState, jdbcType=INTEGER}


        AND ali.remark = #{query.remark, jdbcType=VARCHAR}


        AND ali.create_area_code = #{query.createAreaCode, jdbcType=VARCHAR}


        AND ali.create_position_code = #{query.createPositionCode, jdbcType=VARCHAR}


        AND ali.create_org_sys_code = #{query.createOrgSysCode, jdbcType=VARCHAR}


        AND ali.create_system_code = #{query.createSystemCode, jdbcType=VARCHAR}


        AND ali.create_tenant_id = #{query.createTenantId, jdbcType=VARCHAR}


        AND ali.create_user_id = #{query.createUserId, jdbcType=VARCHAR}


        AND ali.create_user_name = #{query.createUserName, jdbcType=VARCHAR}


        AND ali.create_time >= #{query.startTime, jdbcType=TIMESTAMP}


        AND ali.create_time <= #{query.endTime, jdbcType=TIMESTAMP}


        AND ali.update_user_id = #{query.updateUserId, jdbcType=VARCHAR}


        AND ali.update_user_name = #{query.updateUserName, jdbcType=VARCHAR}


        AND ali.update_time = #{query.updateTime, jdbcType=TIMESTAMP}


        AND ali.del_flag = #{query.delFlag, jdbcType=INTEGER}

SELECT

FROM act_custom_design_form_detail

    del_flag = 0

        AND form_detail_id = #{formDetailId, jdbcType=VARCHAR}


        AND from_filed_name = #{fromFiledName, jdbcType=VARCHAR}


        AND form_filed_type = #{formFiledType, jdbcType=INTEGER}


        AND form_filed_label = #{formFiledLabel, jdbcType=VARCHAR}


        AND form_filed_must = #{formFiledMust, jdbcType=INTEGER}


        AND form_filed_error_msg = #{formFiledErrorMsg, jdbcType=VARCHAR}


        AND form_filed_sort = #{formFiledSort, jdbcType=INTEGER}


        AND form_filed_state = #{formFiledState, jdbcType=INTEGER}


        AND remark = #{remark, jdbcType=VARCHAR}


        AND create_area_code = #{createAreaCode, jdbcType=VARCHAR}


        AND create_position_code = #{createPositionCode, jdbcType=VARCHAR}


        AND create_org_sys_code = #{createOrgSysCode, jdbcType=VARCHAR}


        AND create_system_code = #{createSystemCode, jdbcType=VARCHAR}


        AND create_tenant_id = #{createTenantId, jdbcType=VARCHAR}


        AND create_user_id = #{createUserId, jdbcType=VARCHAR}


        AND create_user_name = #{createUserName, jdbcType=VARCHAR}


        AND create_time = #{createTime, jdbcType=TIMESTAMP}


        AND update_user_id = #{updateUserId, jdbcType=VARCHAR}


        AND update_user_name = #{updateUserName, jdbcType=VARCHAR}


        AND update_time = #{updateTime, jdbcType=TIMESTAMP}


        AND del_flag = #{delFlag, jdbcType=INTEGER}

mapper.delete:
DELETE
FROM act_custom_design_form_detail
WHERE form_detail_id = #{id, jdbcType=VARCHAR}

mapper.update:
UPDATE act_custom_design_form_detail

    del_flag = 1,

WHERE form_detail_id = #{formDetailId, jdbcType=VARCHAR}
