mapper.select:
SELECT

FROM storage_info_file ali

     ali.del_flag = 0

      AND LOCATE(#{query.fileOriginalName, jdbcType=VARCHAR}, ali.file_original_name) > 0


     AND ali.file_storage_type = #{query.fileStorageType, jdbcType=INTEGER}


     AND ali.create_time >= #{query.startTime, jdbcType=TIMESTAMP}


     AND ali.create_time <= #{query.endTime, jdbcType=TIMESTAMP}

mapper.delete:
DELETE
FROM storage_info_file
WHERE file_id = #{id, jdbcType=VARCHAR}

mapper.update:
UPDATE storage_info_file

    del_flag = 1,

WHERE file_id = #{fileId, jdbcType=VARCHAR}
