mapper.select:
SELECT

FROM sys_common_dict

    del_flag = 0

        AND (
        LOCATE(#{query.keyword, jdbcType=VARCHAR}, dict_name) > 0
        OR
        LOCATE(#{query.keyword, jdbcType=VARCHAR}, dict_code) > 0
        )


        AND dict_status = #{query.dictStatus, jdbcType=VARCHAR}


        AND dict_type = #{query.dictType, jdbcType=INTEGER}


        AND create_time >= #{query.startTime, jdbcType=TIMESTAMP}


        AND create_time <= #{query.endTime, jdbcType=TIMESTAMP}

mapper.delete:
DELETE
FROM sys_common_dict
WHERE dict_id = #{id, jdbcType=VARCHAR}

mapper.update:
UPDATE sys_common_dict
del_flag = 1, unique_help = dict_id,
WHERE dict_id = #{dictId, jdbcType=VARCHAR}
