mapper.select:
SELECT

FROM sys_rbac_client_details ali

    ali.del_flag = 0

        AND LOCATE(#{query.clientId, jdbcType=VARCHAR}, ali.client_id) > 0


        AND LOCATE(#{query.clientName, jdbcType=VARCHAR}, ali.client_name) > 0


        AND ali.client_status = #{query.clientStatus, jdbcType=INTEGER}


        AND ali.create_time >= #{query.startTime, jdbcType=TIMESTAMP}


        AND ali.create_time <= #{query.endTime, jdbcType=TIMESTAMP}

mapper.delete:
DELETE
FROM sys_rbac_client_details
WHERE client_detail_id = #{id, jdbcType=VARCHAR}

mapper.update:
UPDATE sys_rbac_client_details
del_flag = 1, unique_help = client_detail_id,
WHERE client_detail_id = #{clientDetailId, jdbcType=VARCHAR}
