mapper.select:
SELECT

FROM sys_rbac_menu ali

     ali.del_flag = 0

     AND ali.menu_type IN

          #{item}



     AND ali.menu_status = #{query.menuStatus, jdbcType=INTEGER}


    AND LOCATE(#{query.metaTitle, jdbcType=VARCHAR}, ali.meta_title) > 0


     AND ali.create_time >= #{query.startTime, jdbcType=TIMESTAMP}


     AND ali.create_time <= #{query.endTime, jdbcType=TIMESTAMP}

mapper.delete:
DELETE
FROM sys_rbac_menu
WHERE menu_id = #{id, jdbcType=VARCHAR}

mapper.update:
UPDATE sys_rbac_menu
del_flag = 1, unique_help = menu_id,
WHERE menu_id = #{menuId, jdbcType=VARCHAR}
