mapper.select:
SELECT

FROM sys_rbac_position ali

    ali.del_flag = 0

        AND (
        LOCATE(#{query.keyword, jdbcType=VARCHAR}, ali.position_code) > 0
        OR
        LOCATE(#{query.keyword, jdbcType=VARCHAR}, ali.position_name) > 0
        )


        AND ali.position_status = #{query.positionStatus, jdbcType=INTEGER}



        AND ali.create_time >= #{query.startTime, jdbcType=TIMESTAMP}


        AND ali.create_time <= #{query.endTime, jdbcType=TIMESTAMP}

mapper.delete:
DELETE
FROM sys_rbac_position
WHERE position_id = #{id, jdbcType=VARCHAR}

mapper.update:
UPDATE sys_rbac_position
del_flag = 1, unique_help = position_id,
WHERE position_id = #{positionId, jdbcType=VARCHAR}
